let firstName="Łukasz",lastName="Kuś";document.title=`${firstName} ${lastName} - Recruitment task`;const modal=document.querySelector(".modal"),overlay=document.querySelector(".overlay"),modalOpen=document.querySelector(".btn--modal-open"),modalClose=document.querySelector(".modal__close-btn");modalOpen.addEventListener("click",()=>{modal.classList.add("active"),overlay.classList.add("active")}),modalClose.addEventListener("click",()=>{modal.classList.remove("active"),overlay.classList.remove("active")}),overlay.addEventListener("click",()=>{modal.classList.remove("active"),overlay.classList.remove("active")});